import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getAll } from "../BooksAPI";
import { useHistory } from 'react-router-dom';

function Shelf() {
  const [books, setBooks] = useState([]);
  const history = useHistory();

  useEffect(() => {
    getAll()
      .then((data) => {
        setBooks(data);
      })
      .catch((error) => {
        console.log(error); // Xử lý lỗi khi fetch gặp vấn đề
      });
  }, []);

  const onMoveShelf = (book, newShelf) => {
    if (book.shelf !== newShelf) {
      const updatedBooks = books.map((item) => {
        if (item.id === book.id) {
          return { ...item, shelf: newShelf };
        }
        return item;
      });
      setBooks(updatedBooks);
    }
  }

  function handleNavigate(bookId) {
    console.log('aaa'+bookId);
    history.push(`/books/${bookId}`);
  }

  return (
    <div className="list-books">
      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>
      <div className="list-books-content">
        <div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Currently Reading</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {books &&
                  books.map(
                    (book) =>
                      book.shelf === "currentlyReading" && (
                        <li key={book.id}>
                          <div className="book" onClick={() => handleNavigate(book.id)}>
                            <div className="book-top">
                              <div
                                className="book-cover"
                                style={{
                                  width: 128,
                                  height: 193,
                                  backgroundImage: `url(${book.imageLinks.thumbnail})`,
                                }}
                              ></div>
                              <div className="book-shelf-changer">
                                <select
                                  defaultValue={book.shelf}
                                  onChange={(e) =>
                                    onMoveShelf(book, e.target.value)
                                  }
                                >
                                  <option value="move" disabled>
                                    Move to...
                                  </option>
                                  <option value="currentlyReading">
                                    Currently Reading
                                  </option>
                                  <option value="wantToRead">
                                    Want to Read
                                  </option>
                                  <option value="read">Read</option>
                                  <option value="none">None</option>
                                </select>
                              </div>
                            </div>
                            <div className="book-title">{book.subtitle}</div>
                            <div className="book-authors">
                              {book.authors.join(", ")}
                            </div>
                          </div>
                        </li>
                      )
                  )}
              </ol>
            </div>
          </div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Want to Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {books &&
                  books.map(
                    (book) =>
                      book.shelf === "wantToRead" && (
                        <li key={book.id}>
                          <div className="book">
                            <div className="book-top">
                              <div
                                className="book-cover"
                                style={{
                                  width: 128,
                                  height: 193,
                                  backgroundImage: `url(${book.imageLinks.thumbnail})`,
                                }}
                              ></div>
                              <div className="book-shelf-changer">
                                <select
                                  defaultValue={book.shelf}
                                  onChange={(e) =>
                                    onMoveShelf(book, e.target.value)
                                  }
                                >
                                  <option value="move" disabled>
                                    Move to...
                                  </option>
                                  <option value="currentlyReading">
                                    Currently Reading
                                  </option>
                                  <option value="wantToRead">
                                    Want to Read
                                  </option>
                                  <option value="read">Read</option>
                                  <option value="none">None</option>
                                </select>
                              </div>
                            </div>
                            <div className="book-title">{book.subtitle}</div>
                            <div className="book-authors">
                              {book.authors.join(", ")}
                            </div>
                          </div>
                        </li>
                      )
                  )}
              </ol>
            </div>
          </div>
          <div className="bookshelf">
            <h2 className="bookshelf-title">Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {books &&
                  books.map(
                    (book) =>
                      book.shelf === "read" && (
                        <li key={book.id}>
                          <div className="book">
                            <div className="book-top">
                              <div
                                className="book-cover"
                                style={{
                                  width: 128,
                                  height: 193,
                                  backgroundImage: `url(${book.imageLinks.thumbnail})`,
                                }}
                              ></div>
                              <div className="book-shelf-changer">
                                <select
                                  defaultValue={book.shelf}
                                  onChange={(e) =>
                                    onMoveShelf(book, e.target.value)
                                  }
                                >
                                  <option value="move" disabled>
                                    Move to...
                                  </option>
                                  <option value="currentlyReading">
                                    Currently Reading
                                  </option>
                                  <option value="wantToRead">
                                    Want to Read
                                  </option>
                                  <option value="read">Read</option>
                                  <option value="none">None</option>
                                </select>
                              </div>
                            </div>
                            <div className="book-title">{book.subtitle}</div>
                            <div className="book-authors">
                              {book.authors.join(", ")}
                            </div>
                          </div>
                        </li>
                      )
                  )}
              </ol>
            </div>
          </div>
        </div>
      </div>
      <div className="open-search">
        <Link to={"/search"}>Add a book</Link>
      </div>
    </div>
  );
}

export default Shelf;
